<?phpclass ManageController extends BaseController{	public function __construct(){		$this->beforeFilter('auth');		$this->beforeFilter('csrf',array('on'=>'post'));		$this->beforeFilter("permission:moderator");	}	public function getBookings(){		$bookings=Booking::orderBy("from","DESC")->get();		return View::make("manage/bookings",array("bookings"=>$bookings));	}		public function getUsers(){		$users=User::all();		return View::make("manage/users",array("users"=>$users));	}}